import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

export default function Details() {
  const [data, setData] = useState([]);
  const { id } = useParams();

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:8087/supplier/fetch/${id}`);
      setData(response.data);
      consol
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, [id]);

  return (
    <div>
      <h1>Data Table</h1>
      {data.length > 0 ? (
        <table>
          <thead>
            <tr>
              <th>Type</th>
              <th>Name</th>
              <th>Price</th>
              <th>Quantity</th>
            </tr>
          </thead>
          <tbody>
            {data.map((d, i) => (
              <tr key={i}>
                <td>{d._id}</td>
                <td>{d.name}</td>
                <td>{d.price}</td>
                <td>{d.quantity}</td>
                <td>
                  <Link className='container' to={`/update/${d._id}`}>Update</Link>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
}
