import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

export default function Details() {
  const [data, setData] = useState([]);
  const { id } = useParams();

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:8087/supplier/fetch/${id}`);
      setData(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, [id]);
  const handleDelete = async (itemId) => {
    try {
      await axios.delete(`http://localhost:8087/items/${itemId}`);
      // After successful deletion, fetch items again to update the list
      fetchItems();
    } catch (error) {
      console.error('Error deleting item:', error);
    }
  };

  return (
    <div>
      <h1>Data Table</h1>
  
      <table>
        <thead>
          <tr>
            <th>Type</th>
            <th>Name</th>
            <th>Price</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
           
              <tr  >
                <td>{data.type}</td>
                <td>{data.name}</td>
                <td>{data.price}</td>
                <td>{data.quantity}</td>
                <td>
                  <Link className='container' to={`/update/${data._id}`}>Update</Link>
                </td>
                 <td> 
                <button onClick={() => handleDelete( data._id)}>Delete</button></td>
              </tr>
           
           
       
        </tbody>
      </table>
    </div>
  );
}
