export default function Details() {
  const [data, setData] = useState([]);
  const { id } = useParams();

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:8087/supplier/fetch/${id}`);
      setData(response.data);
      console.log(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, [id]);

  return (
    <div>
      <h1>Data Table</h1>
  
      <table>
        <thead>
          <tr>
            <th>Type</th>
            <th>Name</th>
            <th>Price</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {Array.isArray(data) ? (
            data.map((d, i) => (
              <tr key={i}>
                <td>{d.type}</td>
                <td>{d.name}</td>
                <td>{d.price}</td>
                <td>{d.quantity}</td>
                <td>
                  <Link className='container' to={`/update/${d._id}`}>Update</Link>
                </td>
              </tr>
            ))
          ) : (
            <tr>
              <td colSpan="5">No data available</td>
            </tr>
          )}
        </tbody>
      </table>
    </div>
  );
}
