import axios from 'axios'
import react from 'react'
import {usestate} from 'react'
import {useEffect} from 'react'        
  
export default function details(){
  const Details = () => { 
             const [data,setData]=usestate([]);    

             useEffect(()=>{
                 axios.get( 'http ://localhost :3000/fetch  ')   
                 .then(res => setData (res.data )  ) 
                 .catch(err =>console.log(err))} ,[])
              
             
              
             const handleUpdateClick = (id) => {
              // Redirect to the update page with the specific ObjectId
              window.location = `frontend/src/componenets/updatedetails/${id}`;
            };
             return (
              <div>
                <h1>Data Table</h1>
                <table>
                  <thead>
                    <tr>
                      <th>Type </th>
                      <th> Name</th>
                      <th>Price</th>
                      <th>Quantity</th>
                      {/* Add more table headers if needed */}
                    </tr>
                  </thead>
                  <tbody>
                    {data.map(item => (
                      <tr key={item_id   }>
                        <td>{item.tea_type}</td>
                        <td>{item.name}</td>
                        <td>{item.price }</td>
                        <td>{item.quantity}</td>
                          
                        
                        <button onClick={() => handleUpdateClick( item_id)}>Update</button>
                         
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            );
                    
                    }
                  }
