import React, { useState } from 'react';
import axios from 'axios';
import details from './components/details';
export default function updatedetails (){ 
  const details = ({ id }) => {
   
               
     
  const UpdatedDetails = ({ id,initialname, initialPrice }) => {
    let price = initialPrice;   
    let name=initialname;  
     
    console.log(`The price of ${name} is ${price}`);
  }
  const [updatedData, setUpdatedData] = useState( {UpdatedDetails} )
               
            
           
           
   
  const handleUpdate = async () => {
    try {
      const response = await axios.put(`/api/data/${id}`, { updatedData   });
      console.log('Updated:', response.data);
    } catch (error) {
      console.error('Error updating data:', error);
    }
  };

  return (
    <div>
        <lable>Tea name</lable>
      <input
        type="text"
        value=  {$name}      
        onChange={(e) => setUpdatedData(e.target.value)}
      />
      <input type="text" value={$price}  onChange={(e)=>setUpdatedData(e.target.value)}/> 
      <button onClick={handleUpdate}>Update</button>
    </div>
  );
};
}
