import React ,{useEffect} from 'react';
import   { useState } from 'react';
import axios from 'axios' ;
import {useNavigate, useParams} from 'react-router-dom' ;    

export default function Update(){
    const {id}=useParams();  
    const[values,setValues ]=useState({
        id :id,
        name:'',
        price:''
    })    
    useEffect(()=>{
                axios.get( `http://localhost:3000/fetch/${id}`)                 
        .then(res => {setValues({...values,name :res.data.name,price:res.data.price})} )   
        .catch(err =>console.log(err))} ,[id,values]) 
          const navigate = useNavigate();
      const  handlesubmit =(e) =>{
                e.preventDefault ();
                axios.post(`http://localhost: 8/update/${id}`,values)    
                .then(res =>{
                      navigate('/details');

                })
                .catch(err =>console.log(err));
        }
     
     return(
         <div  classname="container">
             
             <form>      
                 <div>
                    <lable htmlfor="name "  >Name</lable>
                    <input type="text" name="name " className='form-control' placeholder='enter name'  value={values.name}   onChange={e=>setValues({...values,name:e.target.value})  } />  
                 </div>
                 <div>
                    <lable htmlfor=" price "  >Price</lable>
                    <input type="text" name="price  " className='form-control' placeholder='enter price ' value={values.price}  onChange={e=>setValues({...values, price:e.target.value})  }      / >  
                 </div><br/>
                 <button  className   ='btn btn info'  onClick={handlesubmit}   > Update</button>
                 {/* </br> */}
             </form>
             
         </div>
     )
}
