import   { useState } from 'react';
 
function Search (){
          const[value,setvalue]=useState ('');
           const[data,setdata ]=useState ([]);
           const onChange = async (e) => {
               const searchValue = e.target.value;
                 setvalue(searchValue);
               try {
                 const response = await fetch(`http://localhost:8087/supplier/search?q=${searchValue}`);
                 const searchData = await response.json();
                 console.log(searchData); // Log the data variable
                    ata(searchData);
               } catch (error) {
                 console.error('Error searching suppliers:', error);
               }
             };
             
             
     return(
          <div>  
         <div  classname='container'> 
              <input type="text"  onChange={onChange} value={value}></input> 
              <button>Search</button>   
         </div>
           <div> {  
             value &&
                      data.filter( item  =>item.title.startsWith (value)).map(item =><div key ={ item.id}  >{item.title}<hr/></div>)   
}</div>
          </div> 
           
     )
}
export default Search ;
