import React ,{useEffect} from 'react';
import   { useState } from 'react';
import axios from 'axios' ;
import {useParams}    from  'react-router-dom' ; 

export default function  Update(){
    const {id}=useParams();  
    const[values,setValues ]=useState({
        id :id,
        name:'',
        price:''
    })    
    useEffect(()=>{
        axios.get(  'http ://localhost :3000/fetch'+id)        
        .then(res => {setValues({...values,name :res.data.name,price:res.data.price})}   ) 
        .catch(err =>console.log(err))} ,[])
     
     return(
         <div  classname="container">
            <div  classname="" >
             <form>
                 <div>
                    <lable htmlfor="name "  >Name</lable>
                    <input type="text" name="name " className='form-control' placeholder='enter name'  value={values.name}/ >  
                 </div>
                 <div>
                    <lable htmlfor=" price "  >Price</lable>
                    <input type="text" name="price  " className='form-control' placeholder='enter price ' value={values.price}/ >  
                 </div><br>
                 <button  className   ='btn btn info' > Update</button>
                 </br>
             </form>
            </div>
         </div>
     )
}
