import axios from 'axios';
import { useState, useEffect } from 'react';
import { Link, useParams } from 'react-router-dom';

export default function Details() {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const { id } = useParams();

  const fetchData = async () => {
    try {
      const response = await axios.get(`http://localhost:8087/supplier/fetch/${id}`);
      setData(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  useEffect(() => {
    fetchData();
  }, [id]);

  return (
    <div>
      <h1>Point of Sale Output</h1>
  
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div>
          <h2>Item Details</h2>
          <table>
            <tbody>
              <tr>
                <td>Type:</td>
                <td>{data.type}</td>
              </tr>
              <tr>
                <td>Name:</td>
                <td>{data.name}</td>
              </tr>
              <tr>
                <td>Price:</td>
                <td>${data.price}</td>
              </tr>
              <tr>
                <td>Quantity:</td>
                <td>{data.quantity}</td>
              </tr>
            </tbody>
          </table>
          <h2>Total Price</h2>
          <p>${data.price * data.quantity}</p>
        </div>
      )}
    </div>
  );
}
